//==================================================================================================
// case statement corresponds with each of the structures in the BNF.
//  (this could just as easily be implemented as a big if/else block)
function walk(obj) {

  /* preorder */
	printNode(obj);

	treeDepth += 1;

  /* inorder */	
	switch(obj.type){

	// TESTING PURPOSES ONLY - TODO: remove test node
		case "test":
			walkTest(obj);
			break
	// program root
		case "program":
			walkProgram(obj);
			break;
	// assignment
		case "assign":
			walkAssign(obj);
			break;
	// declarations (inner and outer)
		case "function":
			walkFunction(obj);
			break;
		case "variable-decl-assign":		// TODO: ensure consistency - multiword nodes: underscores or hyphens?
			walkVariableDeclAssign(obj);
			break;
		case "variable-decl":
			walkVariableDecl(obj);
			break;
	// control structures
		case "if":
			walkIf(obj);
			break;
		case "ifelse":			// TODO: ensure consistency - multiword nodes: underscores or hyphens?
			walkIfElse(obj);
			break;
		case "while":
			walkWhile(obj);
			break;
		case "do_while":		// TODO: ensure consistency - multiword nodes: underscores or hyphens?
			walkDoWhile(obj);
			break;
		case "for":
			walkFor(obj);
			break;
	// mathematical operations
		case "addition":
			walkAddition(obj);
			break;
		case "minus":
			walkMinus(obj);
			break;
		case "multiplication":
			walkMultiplication(obj);
			break;
		case "division":
			walkDivision(obj);
			break
		case "modulo":
			walkModulo(obj);
			break;
	// shorthand operations		// TODO: ensure consistency - multiword nodes: underscores or hyphens?
	// 	--assignment
		case "add_assign":
			walkAddAssign(obj);
			break;
		case "sub_assign":
			walkSubAssign(obj);
			break;
		case "multi_assign":
			walkMultiAssign(obj);
			break;
		case "div_assign":
			walkDivAssign(obj);
			break;
		case "mod_assign":
			walkModAssign(obj);
			break;
	// 	--increment/decrement
		case "increment":
			walkIncrement(obj);
			break;
		case "decrement":
			walkDecrement(obj);
			break;
	// boolean logic
		case "and":
			walkAnd(obj);
			break;
		case "or":
			walkOr(obj);
			break;
	// comparison operations		// TODO: ensure consistency - multiword nodes: underscores or hyphens?
		case "equality":
			walkEquality(obj);
			break
		case "less-than":
			walkLessThan(obj);
			break;
		case "larger-than":		// TODO : consistency - larger than OR greater than, not both.
			walkLargerThan(obj);
			break;
		case "not-equal":
			walkNotEqual(obj);
			break;
		case "less-than-or-equal":
			walkLessThanOrEqual(obj);
			break;
		case "greater-than-or-equal":
			walkGreaterThanOrEqual(obj);
			break;	
	// bitwise operations		// TODO: ensure consistency - multiword nodes: underscores or hyphens?
		case "bit-XOR":
			walkBitXOR(obj);
			break;
		case "bit-AND":
			walkBitAND(obj);
			break;
		case "bit-OR":
			walkBitOR(obj);
			break;
		case "bit-right-shift":
			walkBitRightShift(obj);
			break;
		case "bit-left-shift":
			walkBitLeftShift(obj);
			break;
		case "zero-fill-right-shift":
			walkZeroFillRightShift(obj);
			break;
			
	// "OH NO"des - if either of these two is reached, then something has gone horribly wrong.
	/*
	 *	This would happen if we haven't accounted for something in the parser.
	 *	To account for the error, we keep it at the same depth as its parent, 
	 *  which we've done here by decrementing/incrementing the treeDepth
	 *	in pre/postorder traversal respectively.
	 */
	 	
	// undefined -- usually an empty node, e.g. a blank program.
		case undefined:
			treeDepth -= 1;
			walkUndefined();
			treeDepth += 1;
			break;
	// default - this means that something unexpected has got into the tree.
		default :
			treeDepth -= 1;
			walkUnknown(obj);
			treeDepth += 1;
			break;
	}

  /* postorder */
	treeDepth -= 1;
	
	printNode(obj);
	
}
